// To federated sign in from Facebookconst SignInWithFacebook = () => {
  useEffect(() => {    if (!window.FB) createScript();  }, [])
  const signIn = () => {    const fb = window.FB;    fb.getLoginStatus(response => {      if (response.status === 'connected') {        getAWSCredentials(response.authResponse);      } else {        fb.login(          response => {            if (!response || !response.authResponse) {              return;            }            customCredentialsProvider.loadFederatedLogin({              domain: 'graph.facebook.com',              token: response.authResponse.accessToken,            });            const fetchSessionResult = await fetchAuthSession(); // will return the credentials            console.log('fetchSessionResult: ', fetchSessionResult);          },          {            // the authorized scopes            scope: 'public_profile,email'          }        );      }    });  }
  const createScript = () => {    // load the sdk    window.fbAsyncInit = fbAsyncInit;    const script = document.createElement('script');    script.src = 'https://connect.facebook.net/en_US/sdk.js';    script.async = true;    script.onload = initFB;    document.body.appendChild(script);  }
  const initFB = () => {    const fb = window.FB;    console.log('FB SDK initialized');  }
  const fbAsyncInit = () => {    // init the fb sdk client    const fb = window.FB;    fb.init({      appId   : 'your_facebook_app_id',      cookie  : true,      xfbml   : true,      version : 'v2.11'    });  }
  return (    <div>      <button onClick={signIn}>Sign in with Facebook</button>    </div>  );}

import type { EventsChannel } from 'aws-amplify/data';import { useState, useEffect, useRef } from 'react';import { Amplify } from 'aws-amplify';import { events } from 'aws-amplify/data';
Amplify.configure({  API: {    Events: {      endpoint:        'https://abcdefghijklmnopqrstuvwxyz.appsync-api.us-east-1.amazonaws.com/event',      region: 'us-east-1',      defaultAuthMode: 'apiKey',      apiKey: 'da2-abcdefghijklmnopqrstuvwxyz',    },  },});
export default function App() { const [myEvents, setMyEvents] = useState<unknown[]>([]);
  const sub = useRef<ReturnType<EventsChannel['subscribe']>>(null);
  useEffect(() => {    let channel: EventsChannel;
    const connectAndSubscribe = async () => {      channel = await events.connect('default/channel');
      if (!sub.current) {        sub.current = channel.subscribe({          next: (data) => {            console.log('received', data);            setMyEvents((prev) => [data, ...prev]);          },          error: (err) => console.error('error', err),        });      }    };
    connectAndSubscribe();
    return () => {      sub.current?.unsubscribe();      sub.current = null;      return channel?.close();    };  }, []);
  async function publishEvent() {    // Publish via HTTP POST    await events.post('default/channel', { some: 'data' });
    // Alternatively, publish events through the WebSocket channel    const channel = await events.connect('default/channel');    await channel.publish({ some: 'data' });  }
  return (    <>      <button onClick={publishEvent}>Publish Event</button>      <ul>        {myEvents.map((data, idx) => (          <li key={idx}>{JSON.stringify(data.event, null, 2)}</li>        ))}      </ul>    </>  );}
