  return (    <AIConversation      messages={messages}      isLoading={isLoading}      handleSendMessage={sendMessage}      // This will let the LLM know about the current state of this application      // so it can better respond to questions      aiContext={() => {        return {          currentTime: new Date().toLocaleTimeString(),        };      }}    />  );}
// Create a context to share state across componentsconst DataContext = React.createContext<{  data: any;  setData: (value: React.SetStateAction<any>) => void;}>({ data: {}, setData: () => {} });
// Create a component that updates the shared statefunction Counter() {  const { data, setData } = React.useContext(AIContext);  const count = data.count ?? 0;  return (    <Button onClick={() => setData({ ...data, count: count + 1 })}>      {count}    </Button>  );}
// reference shared data in aiContextfunction Chat() {  const { data } = React.useContext(DataContext);  const [    {      data: { messages },      isLoading,    },    sendMessage,  ] = useAIConversation('pirateChat');
  return (    <AIConversation      messages={messages}      isLoading={isLoading}      handleSendMessage={sendMessage}      // This will let the LLM know about the current state of this application      // so it can better respond to questions      aiContext={() => {        return {          ...data,          currentTime: new Date().toLocaleTimeString(),        };      }}    />  );}
export default function Example() {  const [data, setData] = React.useState({});  return (    <Authenticator>      <DataContext.Provider value={{ data, setData }}>        <Counter />        <Chat />      </DataContext.Provider>    </Authenticator>  )}

